import React, { Component } from 'react';
import SearchMovies from '../components/SearchMovies.js';
import NavBar from '../components/NavBar.js';
import MovieCard from '../components/MovieCard.js';
import './App.css';

class App extends Component {
  constructor() {
    super();
    this.state = {
      query:'' ,
      movies:[]
    }
  }

  componentDidMount() {
    const url = `https://api.themoviedb.org/3/search/movie?api_key=c28333c97f27ab72531d6065d408f418&language=en-US&query=${this.state.query}&page=1&include_adult=false`;    
    try{
      fetch(url)
      .then(response => response.json())
      .then(movies => console.log(movies))
    } catch(error) {
      console.log(error);
    }
  }

  onSearchChange = event => {
    return this.setState({query: event.target.value});
  }

  render() {
    return (
    <div>
      <div className="fragment">
        <NavBar />
        <div className="search-container">
          <h1 className="title-cta">Search Now.</h1>
          <h2 className="title">Want to find out more about your favorite movies?</h2>
          <SearchMovies />
        </div>
      </div>
      <div className="cards container">
        <MovieCard />
      </div>
    </div>
    );
  }
}

export default App;
