import React, { Component } from 'react';
import SearchMovies from '../components/SearchMovies.js';
import NavBar from '../components/NavBar.js';
import MovieCardList from '../components/MovieCardList.js';
import './App.css';

class App extends Component {
  constructor() {
    super();
    this.state = {
      query:'',
      movies:[],
    }
  }

  onSubmitChange = (e) => {
    e.preventDefault(); //必要！！避免瀏覽器儲存query的預設行為（不是很懂為什麼...但沒加就會失敗） 
    const { query, movies } = this.state;
    const url = `https://api.themoviedb.org/3/search/movie?api_key=c28333c97f27ab72531d6065d408f418&language=en-US&query=${query}&page=1&include_adult=false`;    
    try{
      fetch(url)
      .then(response => response.json())
      .then(data => this.setState({ movies: data.results }));
    } catch(error) {
      console.log(error);
    }
  }

  onSearchChange = (event) => {
    this.setState({ query: event.target.value });
  }

  render() {
    return (
    <div>
      <div className="fragment">
        <NavBar />
        <div className="search-container">
          <h1 className="title-cta">Search Now.</h1>
          <h2 className="title">Want to find out more about your favorite movies?</h2>
          <SearchMovies searchChange={ this.onSearchChange } submitChange={ this.onSubmitChange }/>
        </div>
      </div>
      <MovieCardList/>
    </div>
    );
  }
}

export default App;
